package arrpack;


public class Arrcopy 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int[] src;
		int[] dest;
		src= new int[5];
		dest= new int[5];
		src[2]=3;
		src[3]=5;
		src[4]=10;
		
System.arraycopy(src,1,dest,2,3 );
for(int data: dest)
		System.out.println(data);







































































































}


}
